mapper.update:

update log
set user_name = #{userName,jdbcType=VARCHAR},
role_name = #{roleName,jdbcType=VARCHAR},
ip = #{ip,jdbcType=VARCHAR},
busi = #{busi,jdbcType=VARCHAR},
oper_type = #{operType,jdbcType=INTEGER},
method = #{method,jdbcType=VARCHAR},
start_time = #{startTime,jdbcType=VARCHAR},
end_time = #{endTime,jdbcType=VARCHAR},
status_cd = #{statusCd,jdbcType=INTEGER}
req_data = #{reqData,jdbcType=VARCHAR}
resp_data = #{statusCd,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
SELECT COUNT(1)
FROM log as l


    status_cd = 1


    and oper_type = #{operType}



    and userName LIKE '%' #{userName} '%'



    and busi  LIKE '%' #{busi} '%'



    and end_time >  #{startTime}



    and end_time <  #{endTime}

SELECT user_name AS userName, role_name AS roleName, ip AS ip, busi AS busi, oper_type AS operType, method AS method, start_time AS startTime, end_time AS endTime, req_data AS reqData, resp_data AS respData FROM log as l
status_cd = 1
and oper_type = #{operType}
and userName LIKE '%' #{userName} '%'
and busi LIKE '%' #{busi} '%'
and end_time > #{startTime}
and end_time < #{endTime}
ORDER BY start_time desc
